import React, { useRef, useEffect } from "react";
import "./Map.css";

const Map = (props) => {
    const mapRef = useRef();

    useEffect;
    const map = new window.googel.maps.Map(mapRef.current, {
        center: props.center,
        zoom: props.zoom,
    });

    // create a marker in center of our map
    new window.googel.maps.Marker({ position: props.center, map: map });
    return (
        <div
            ref={mapRef}
            className={`map ${props.className}`}
            style={props.style}
        ></div>
    );
};

export default Map;
