const Input = (props) => {
    const onChangeHandler = () => {};

    const element =
        props.element === "input" ? (
            <input
                id={props.id}
                type={props.type}
                placeholder={props.placeholder}
                onChange={onChangeHandler}
            />
        ) : (
            <textarea
                id={props.id}
                rows={props.rows || 3}
                onChange={onChangeHandler}
            />
        );
    return (
        <div className={`form-control`}>
            <label htmlFor={props.id}>{props.lable}</label>
            {element}
        </div>
    );
};

export default Input;
